
public class Invoice {

	static int distance;
	static int time;

	public Invoice(int dis, int time) {
		// TODO Auto-generated constructor stub
		this.distance = dis;
		this.time = time;
	}

	public void setDistance(int distance) {
		this.distance = distance;
	}

	public int getDistance() {
		return distance;
	}

	public int getTime() {
		return time;
	}

	public void setTime(int time) {
		this.time = time;
	}
}
